mapper.select:
SELECT e.name, t.team_name , d.*, a.* FROM document d INNER JOIN approval a ON d.document_no = a.document_no INNER JOIN emp e ON d.emp_no = e.emp_no INNER JOIN team t ON e.team_no = t.team_no
AND d.document_no = #{document_no}
order by a.document_no desc

mapper.update:
UPDATE document
SET state = #{state}
WHERE document_no = #{document_no}
