mapper.select:
SELECT * FROM document LEFT JOIN approval ON document.document_no = approval.document_no
AND document.emp_no = ${emp_no}
AND document.state = '임시저장' AND document.document_no = ${document_no}
AND document.state != '임시저장'
order by document.document_no desc

SELECT e.name, t.team_name , d.*, a.* FROM document d INNER JOIN approval a ON d.document_no = a.document_no INNER JOIN emp e ON d.emp_no = e.emp_no INNER JOIN team t ON e.team_no = t.team_no
AND d.document_no = #{document_no}
order by a.document_no desc

mapper.update:
UPDATE document
SET state = #{state}
WHERE document_no = #{document_no}
