mapper.update:
update emp
set team_no = #{team_no}
  ,emp_access = #{emp_access}
  ,emp_position = #{emp_position}
  ,sign_no = 0
  ,dayoff_cnt = 0
  ,emp_year = 0
  ,hire_date = #{hire_date}
  ,emp_state = #{emp_state}
  ,password = #{password}
  ,name = #{name jdbcType}
  ,email = #{email jdbcType}
  ,birthdate = #{birthdate}
  ,phone_num = #{phone_num}
  ,mod_date = to_char(sysdate,'YYYY-MM-DD HH24:MI:SS')
  ,address = #{address}
  ,retire_date = #{retire_date}}
,profile_img = null
where emp_no = #{emp_no}
